import React from "react";

function Button(props) {
  const { isIncrease, isFocus, times, setTimes, oioi } = props;

  const { focus, breakTime } = times;
  const interval = isFocus ? focus.interval : breakTime.interval;
  let dataTest = isFocus
    ? isIncrease
      ? "increase-focus"
      : "decrease-focus"
    : isIncrease
    ? "increase-break"
    : "decrease-break";

  const clickHandler = () => {
    isFocus
      ? isIncrease
        ? setTimes({ ...times, [focus.current]: current + interval * 60 })
        : setTimes({ ...times, [focus.current]: current - interval * 60 })
      : isIncrease
      ? setTimes({ ...times, [breakTime.current]: current + interval * 60 })
      : setTimes({ ...times, [breakTime.current]: current - interval * 60 });
  };

  return (
    <button
      type="button"
      className="btn btn-secondary"
      data-testid={dataTest}
      onClick={clickHandler()}
    >
      <span className={oioi} />
    </button>
  );
}

export default Button;
