import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import Button from "../button/Button";
import { minutesToDuration, secondsToDuration } from "../utils/duration";
import TimerController from "../button/TimerController";
import Display from "../display/Display";

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [times, setTimes] = useState({
    focus: { max: 60, min: 5, default: 25 * 60, interval: 5, current: 25 * 60 },
    breakTime: {
      max: 15,
      min: 1,
      default: 5 * 60,
      interval: 1,
      current: 5 * 60,
    },
    hasStarted: false,
    isFocusMode: true,
  });
  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      if (times.focus.current < 1) setTimes({ ...times, isFocusMode: false });
      else if (times.breakTime.current < 1) {
        setTimes({
          ...times,
          breakTime: { ...times.breakTime, current: times.breakTime.default },
          isFocusMode: true,
        });
      } else if (times.isFocusMode) {
        setTimes({
          ...times,
          focus: { ...times.focus, current: times.focus.current - 1 },
        });
      } else if (!times.isFocusMode) {
        setTimes({
          ...times,
          breakTime: {
            ...times.breakTime,
            current: times.breakTime.current - 1,
          },
        });
      }
    },
    isTimerRunning ? 10 : null
  );

  function playPause() {
    if (!times.hasStarted && !isTimerRunning) {
      setTimes({ ...times, hasStarted: true });
    }
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {secondsToDuration(times.focus.current)}
            </span>
            <div className="input-group-append">
              <Button
                isFocus={true}
                isIncrease={false}
                oioi={"oi oi-minus"}
                times={times}
                setTimes={setTimes}
              />
              <Button
                isFocus={true}
                isIncrease={true}
                oioi={"oi oi-plus"}
                times={times}
                setTimes={setTimes}
              />
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {secondsToDuration(times.breakTime.current)}
              </span>
              <div className="input-group-append">
                <Button
                  isFocus={false}
                  isIncrease={false}
                  oioi={"oi oi-minus"}
                  times={times}
                  setTimes={setTimes}
                />
                <Button
                  isFocus={false}
                  isIncrease={true}
                  oioi={"oi oi-plus"}
                  times={times}
                  setTimes={setTimes}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      <TimerController
        times={times}
        isTimerRunning={isTimerRunning}
        playPause={playPause}
        hasStarted={times.hasStarted}
        setTimes={setTimes}
      />
      <Display times={times} />
      {/* <div>
         // TODO: This area should show only when a focus or break session is running or pauses 
        <div className="row mb-2">
          <div className="col">
            // TODO: Update message below to include current session (Focusing or On Break) and total duration 
            <h2 data-testid="session-title">Focusing for 25:00 minutes</h2>
            // TODO: Update message below to include time remaining in the current session 
            <p className="lead" data-testid="session-sub-title">
              25:00 remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div> */}
    </div>
  );
}

export default Pomodoro;
