import React from "react";
import { minutesToDuration, secondsToDuration } from "../utils/duration/index";

function Display(props) {
  const { times } = props;

  function toPercent(part, whole) {
    return 100 - Math.floor((part / whole) * 100);
  }
  let currentTime = null;
  let defaultTime = null;
  if (times.isFocusMode) {
    currentTime = times.focus.current;
    defaultTime = times.focus.current;
  } else {
    currentTime = times.breakTime.current;
    defaultTime = times.breakTime.current;
  }
  let percent = toPercent(currentTime, defaultTime);

  if (times.hasStarted) {
    return (
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">
              {times.isFocusMode
                ? `Focusing for ${minutesToDuration(
                    times.focus.default
                  )} minutes`
                : `On Break for ${minutesToDuration(
                    times.breakTime.default
                  )} minutes`}
            </h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {times.isFocusMode
                ? `${secondsToDuration(times.focus.current)} remaining`
                : `${secondsToDuration(times.breakTime.current)} remaining`}
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={percent} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: `${percent}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
  return null;
}

export default Display;
