function Button(props) {
  const {
    isIncrease,
    isFocus,
    times: { focus, breakTime },
  } = props;

  const interval = isFocus ? focus.interval : breakTime.interval;

  const clickHandler = () => {
    const focusOrBreakTime = isFocus ? "focus" : "breakTime";
    setTime({
      ...times,
      focusOrBreakTime: (isFocus ? focus : breakTime).current - interval * 60,
    });
  };

  return (
    <button
      type="button"
      className="btn btn-secondary"
      data-testid={isFocus ? "decrease-focus" : "decrease-break"}
      onClick={clickHandler()}
    >
      <span className="oi oi-minus" />
    </button>
  );
}

export default Button;
